import React, { useEffect, useState } from 'react';
import { dbService, firebaseInstance } from '../fbase';

function Info() {
    const [info, setInfo] = useState();
    const getInfo = async () =>{
        const dbinfo = await dbService.collection("UKOV").get();
        dbinfo.forEach((document) => {
            const newInfo = {
                ...document.data(),
                id: document.id
            };
            setInfo(newInfo)
        });
        };

    let [newLink, setNewLink] = useState(info?.Link)
    let [newFinish, setNewFinish] = useState(info?.Finish)
    let [newAlumni, setNewAlumni] = useState(info?.Alumni)
    let [newresumePass, setNewresumePass] = useState(info?.resumePass)
    let [newstart_date, setNewstart_date] = useState(info?.start_date)

    let [newproject, setNewproject] = useState(info?.project)

    let [newend_date, setNewend_date] = useState(info?.end_date)
    let [newmeeting1, setNewmeeting1] = useState(info?.meeting1)
    let [newmeeting2, setNewmeeting2] = useState(info?.meeting2)

    const newend_dateChange = (event) => {
        const {target :{value}} = event;
        setNewend_date(value)
    }



    const newmeeting1Change = (event) => {
        const {target :{value}} = event;
        setNewmeeting1(value)
    }



    const newmeeting2Change = (event) => {
        const {target :{value}} = event;
        setNewmeeting2(value)
    }



    const newprojectChange = (event) => {
        const {target :{value}} = event;
        setNewproject(value)
    }



    const newresumePassChange = (event) => {
        const {target :{value}} = event;
        setNewresumePass(value)
    }



    const newstart_dateChange = (event) => {
        const {target :{value}} = event;
        setNewstart_date(value)
    }




    const newLinkChange = (event) => {
        const {target :{value}} = event;
        setNewLink(value)
    }
    const newFinishChange = (event) => {
        const {target :{value}} = event;
        setNewFinish(value)
    }
    const newAlumniChange = (event) => {
        const {target :{value}} = event;
        setNewAlumni(value)
    }

    const onSubmit = (event) => {
        event.preventDefault();
        dbService.collection("test").doc("ukovInfo").update({
            'Finish': firebaseInstance.firestore.Timestamp.fromDate(new Date(newFinish)),
            'Link': newLink,
            'alumni': "fail",
            'end_date': "",
            'start_date': "",
            'file': "",
            'meeting1': "",
            'meeting2': "",
            'project': 23,
            'resumePass': '',
            'th': 24,
            'year': 32,
        })
    }

    useEffect(()=>{
        getInfo();         
    }, [])

    return (
        <>
                <div>페이지에 들어갈 전반적인 정보들을 수정합니다.</div>

        <form onSubmit={onSubmit}>
        <input value={newLink} onChange={newLinkChange} type='text' placeholder={info?.Link}/>구글 독스, typeform등 서류 받을 링크를 넣어주세요<br/>
        <input value={newFinish} onChange={newFinishChange} type='date' placeholder={info?.Fnish}/>서류 마감날짜를 넣어주세요<br/>
        <input value={newAlumni} onChange={newAlumniChange} type='number' placeholder={info?.alumni}/>지금까지 몇명의 ukov알럼나이가 있나요?<br/>

        <input value={newproject} onChange={newprojectChange} type='number' placeholder={info?.project}/>유코브는 지금까지 몇 개의 프로젝트를 했나요?<br/>


        <input value={newstart_date} onChange={newstart_dateChange} placeholder={info?.start_date}/>언제부터 모집이 시작되나요?<br/>
        <input value={newend_date} onChange={newend_dateChange} placeholder={info?.end_date}/>언제 모집이 마감되나요?<br/>
        <input value={newresumePass} onChange={newresumePassChange} placeholder={info?.resumePass}/>서류 합격 발표일은 언제일까요?<br/>
        <input value={newmeeting1} onChange={newmeeting1Change} placeholder={info?.meeting1}/>첫 번째 면접 날<br/>
        <input value={newmeeting2} onChange={newmeeting2Change} placeholder={info?.meeting2}/>두 번째 면접 날<br/>

        <button onClick={onSubmit}>수정하기</button>
        </form>

        </>
    )
}

export default Info;
