import React, { useEffect, useState } from 'react';
import { dbService, firebaseInstance, storageService } from '../fbase';

function Info() {
    const [info, setInfo] = useState();
    const getInfo = async () =>{
        const dbinfo = await dbService.collection("UKOV").get();
        dbinfo.forEach((document) => {
            const newInfo = {
                ...document.data(),
                id: document.id
            };
            setInfo(newInfo)
        });
    };


    let [newLink, setNewLink] = useState(info?.Link)
    let [newFinish, setNewFinish] = useState(info?.Finish)
    let [newAlumni, setNewAlumni] = useState(info?.Alumni)
    let [newresumePass, setNewresumePass] = useState(info?.resumePass)
    let [newstart_date, setNewstart_date] = useState(info?.start_date)
    let [newproject, setNewproject] = useState(info?.project)
    // let [newfile, setNewfile] = useState(info?.file)
    let [newend_date, setNewend_date] = useState(info?.end_date)
    let [newmeeting1, setNewmeeting1] = useState(info?.meeting1)
    let [newmeeting2, setNewmeeting2] = useState(info?.meeting2)
    let [newth, setNewth] = useState(info?.th)
    let [newyear, setNewyear] = useState(info?.year)
    console.log(info?.Finish.seconds)
    console.log(newFinish)
    const newthChange = (event) => {
        const {target :{value}} = event;
        setNewth(value)
    }

    const newyearChange = (event) => {
        const {target :{value}} = event;
        setNewyear(value)
    }


    // const newfileChange = (event) => {
    //     const {target :{files}} = event;
    //     const resumeFile = files[0]
    //     storageService.ref().child('UKOV_'+info?.th+'기_지원서')
    //     // setNewfile(resumeFile)
    //     // 살려주세요~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

    // }
    const newend_dateChange = (event) => {
        const {target :{value}} = event;
        setNewend_date(value)
    }
    const newmeeting1Change = (event) => {
        const {target :{value}} = event;
        setNewmeeting1(value)
    }
    const newmeeting2Change = (event) => {
        const {target :{value}} = event;
        setNewmeeting2(value)
    }
    const newprojectChange = (event) => {
        const {target :{value}} = event;
        setNewproject(value)
    }
    const newresumePassChange = (event) => {
        const {target :{value}} = event;
        setNewresumePass(value)
    }
    const newstart_dateChange = (event) => {
        const {target :{value}} = event;
        setNewstart_date(value)
    }
    const newLinkChange = (event) => {
        const {target :{value}} = event;
        setNewLink(value)
    }
    const newFinishChange = (event) => {
        const {target :{value}} = event;
        setNewFinish(value)
    }
    const newAlumniChange = (event) => {
        const {target :{value}} = event;
        setNewAlumni(value)
    }

    const onSubmit = async (event) => {
        event.preventDefault();
        await dbService.collection("test").doc('test').update({
            Finish: firebaseInstance.firestore.Timestamp.fromDate(new Date(newFinish)),
            Link: newLink,
            alumni: newAlumni,
            end_date: newend_date,
            start_date: newstart_date,
            file: 'newfile',
            meeting1: newmeeting1,
            meeting2: newmeeting2,
            project: newproject,
            resumePass: newresumePass,
            th: newth,
            year: newyear,
        });
        alert('홈페이지를 확인해보세요 :)')
    }

    useEffect(()=>{
        getInfo();  
    }, [])

    return (
        <>
        <div>페이지에 들어갈 전반적인 정보들을 수정합니다.<br/>
            여기에 해당되는 정보 넣고 수정하기 누르면 홈페이지에 바로 수정됩니당~~~</div><br/><br/>
        <form onSubmit={onSubmit}>
            <input value={newLink} onChange={newLinkChange} placeholder={info?.Link}/>구글 독스, typeform등 서류 받을 링크를 넣어주세요<br/><br/>
            {/* 지원서(docx...)를 넣어주세요<input value={newfile} onChange={newfileChange} type='file'/><br/><br/> */}
            <input value={newth} onChange={newthChange} placeholder={info?.th}/>이제 몇기를 뽑는 것일까요?<br/>
            <input value={newyear} onChange={newyearChange} placeholder={info?.year}/>유코브는 몇년 됐나요?<br/>
            <input value={newAlumni} onChange={newAlumniChange} placeholder={info?.alumni}/>지금까지 몇명의 ukov알럼나이가 있나요?<br/>

            <input value={newproject} onChange={newprojectChange} placeholder={info?.project}/>유코브는 지금까지 몇 개의 프로젝트를 했나요?<br/><br/>

            <p>홈페이지에 그대로 나오니 원하는 대로 적어주시면 됩니다 :)</p>
            <input value={newstart_date} onChange={newstart_dateChange} placeholder={info?.start_date}/>언제부터 모집이 시작되나요?<br/>
            <input value={newend_date} onChange={newend_dateChange} placeholder={info?.end_date}/>언제 모집이 마감되나요?<br/>
            <input value={newresumePass} onChange={newresumePassChange} placeholder={info?.resumePass}/>서류 합격 발표일은 언제일까요?<br/>
            <input value={newmeeting1} onChange={newmeeting1Change} placeholder={info?.meeting1}/>첫 번째 면접 날<br/>
            <input value={newmeeting2} onChange={newmeeting2Change} placeholder={info?.meeting2}/>두 번째 면접 날<br/><br/>

            <p>이건 날짜 데이터 따로 받습니다! 날짜 넣으시면 시간에 맞게 리크루팅 탭에 지원하기 버튼이 생기고 사라집니다</p>

            <input value={newFinish} onChange={newFinishChange} type='date' placeholder={info?.Fnish}/>서류 마감날짜를 넣어주세요<br/>


            <button onClick={onSubmit}>수정하기</button>
        </form>
        </>
    )
}

export default Info;
