import React, { useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import Menu from './menu.png';
import Logo from './logo.png';
import '../css/Navbar.css';
import { NavLink } from 'react-router-dom';
import ScrollToTop from './ScrollToTop';

function Navbar(props) {

  const Mobile = useMediaQuery ({
    query : "(max-width : 1000px)"
    })
  const Web = useMediaQuery ({
    query : "(min-width : 1000px)"
    })

  const [toggle, setToggle] = useState(false)

  return (  
    <>
      {Mobile && 
      <>
        <div onClick={()=>(ScrollToTop)} className='navbar'>
        <header>
          <NavLink exact to='/home'><img className='logo' src={ Logo } alt='logo' /></NavLink>
          <img className='menu' src={ Menu } alt='menu' onClick={ () =>setToggle(!toggle)} />
        </header>
        </div>
        {toggle ?(       
          <div className='moblieNav'>   
          <ul className='mobile'>
            <li><NavLink onClick={()=>setToggle(!toggle)} exact to='/home'>홈</NavLink></li>
            <li><NavLink onClick={()=>setToggle(!toggle)} exact to='/program'>프로그램</NavLink></li>
            <li><NavLink onClick={()=>setToggle(!toggle)} exact to='/recruit'>리크루팅</NavLink></li>
          </ul>
          </div>
        ) : null
        }
      </>
      }
      {Web &&     
            <div className='navbar'>
      <header>
        <NavLink exact to='/home'><img className='logo' src={ Logo } alt='logo' /></NavLink>
        <ul className='web'>
          <li><NavLink exact to='/home'>홈</NavLink></li>
          <li><NavLink exact to='/program'>프로그램</NavLink></li>
          <li><NavLink exact to='/recruit'>리크루팅</NavLink></li>
        </ul>
      </header>
      </div>
      }
    </>
  )
}



export default Navbar;
