import React, {useEffect, useState} from 'react';
import {dbService} from '../fbase.js'

function Info(){

    const [info, setInfo] = useState();

    const getInfo = async () =>{
        const dbinfo = await dbService.collection("UKOV").get();
        dbinfo.forEach((document) => {
            const newInfo = {
                ...document.data(),
                id: document.id
            };
            setInfo(newInfo)
        });
        };        
    console.log(info)

    useEffect(()=>{
        getInfo();
    }, [])

    return (
        <>
        {info?.year}
        </>
    )
}

export default Info;
