import React, { useEffect, useState } from 'react';
import { dbService } from '../fbase';
import '../css/internship.css';


function Internship({userObj}) {

    const [internship, setInternship] = useState([])
    const getInternship = async () =>{
        const dbinternship = await dbService.collection("testinternship").get();
        dbinternship.forEach((document) => {
            const newinternship = {
                ...document.data(),
                id: document.id
            };
            setInternship((prev) => [newinternship, ...prev]);
        });
    };
    console.log(internship)
    useEffect(()=>{
        getInternship();
    }, [])
    const [newInternshipLink, setNewInternshipLink] = useState();
    // const onURLChange = (event) => {
    //     const {target:{value}} = event;
    //     setNewInternshipURL(value)
    // }
    // const [newInternshipURL, setNewInternshipURL] = useState();
    const [newTitle, setNewTitle] = useState();
    const [newSubtitle, setNewSubtitle] = useState();

    const newSubmit = async (event) => {
        event.preventDefault();
        await dbService.collection("testinternship").add({
            title: newTitle,
            subtitle: newSubtitle,
            internshipLink: newInternshipLink,
            createdAt: Date.now(),
        })
        setNewInternshipLink('')
        // setNewInternshipURL('')
        setNewTitle('')
        setNewSubtitle('')
        alert('홈페이지에서 확인해보세요 :)')
    }
    const onLinkChange = (event) => {
        const {target:{value}} = event;
        setNewInternshipLink(value)
    }
    const onTitleChange = (event) => {
        const {target:{value}} = event;
        setNewTitle(value)
    }
    const onSubtitleChange = (event) => {
        const {target:{value}} = event;
        setNewSubtitle(value)
    }

    const Delete = () => {
        const del = window.confirm("지우시겠습니까?");
        if (del) {
         dbService.doc(`testinternship/${internship.id}`).delete()
    }}
    const edit = async () => {
        const edit = window.confirm("수정하시겠습니까?");
        if (edit) {
            await dbService.doc(`testinternship/${internship.id}`).update({

            })

        }
    }
    const [editing, setEditing] = useState(false)
    const editingtoggle = () => setEditing(!editing)

    return (
        <>
            <div className='title'>홈탭의 UKOV후기와 프로그램탭의 스타트업 인턴십 <b>두곳 모두</b> 수정되는 곳입니다!</div>
            <div className='subtitle'> 추가하기 </div>ㅋ

                <form onSubmit={newSubmit}>
                {/* 이미지 파일을 추가하세요<input type='file' value={newInternshipLink}  onClick=''/><br/> */}
                    <input  type='text' value={newInternshipLink} onChange={onLinkChange} placeholder='링크 주소를 입력하세요'/><br/>
                    <input  type='text' value={newTitle} onChange={onTitleChange} placeholder='제목을 입력해주세요'/><br/>
                    <input  type='text' value={newSubtitle} onChange={onSubtitleChange} placeholder='부제를 입력해주세요'/><br/>
                    <input  type='submit' placeholder='추가하기' className='button' value=''/>
                </form>
            <div className='subtitle'>수정하기{internship.id}</div>


            {internship.map((internship)=>(
                <>
                  <div className='listBox' key={internship.id}>
                      <a href={internship.internshipLink}>
                          <div className='smallBox'>
                            <img src={internship.internshipURL} alt='internship'/>
                          </div>
                          <h1>{internship.title}</h1>
                          <h2>{internship.subtitle}</h2>
                      </a>
                    <button onClick={()=>{dbService.doc(`testinternship/${internship.id}`).delete()}}>삭제하기</button>
                    <button onClick={editingtoggle}>수정하기</button>
                    {editing
                    ? 
                    <>
                        <form>
                            <input />
                        </form>
                    </>
                    : null}
                  </div>
                </>
              ))}        
        </>
    )
}

export default Internship;
