import React, { Component } from "react";
import Slider from "react-slick";
import SBVA from './SBVA.png';
import '../css/Carousel.css';



class Carousel extends React.Component {
    state = {
        display: true,
        width: 600
      };
      render() {
        const settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 3,
          slidesToScroll: 1
        };
        return (
          <div>
            <h2> Resizable Collapsible </h2>
            <button
              className="button"
              onClick={() =>
                this.setState({
                  width: this.state.width + 100
                })
              }
            >
              {" "}
              increase{" "}
            </button>
            <button
              className="button"
              onClick={() =>
                this.setState({
                  width: this.state.width - 100
                })
              }
            >
              {" "}
              decrease{" "}
            </button>
            <button
              className="button"
              onClick={() =>
                this.setState({
                  display: !this.state.display
                })
              }
            >
              {" "}
              toggle{" "}
            </button>
            <div
              style={{
                width: this.state.width + "px",
                display: this.state.display ? "block" : "none"
              }}
            >
              <Slider {...settings}>
                <div>
                  <h3>1</h3>
                </div>
                <div>
                  <h3>2</h3>
                </div>
                <div>
                  <h3>3</h3>
                </div>
                <div>
                  <h3>4</h3>
                </div>
                <div>
                  <h3>5</h3>
                </div>
                <div>
                  <h3>6</h3>
                </div>
              </Slider>
            </div>
          </div>
        );
      }
    }

export default Carousel;
