import React, { useEffect, useState } from 'react';
import { dbService } from '../fbase';
import '../css/internship.css';


function Internship() {

    const [internship, setInternship] = useState([])
    const getInternship = async () =>{
        const dbinternship = await dbService.collection("internship").get();
        dbinternship.forEach((document) => {
            const newinternship = {
                ...document.data(),
                id: document.id
            };
            setInternship((prev) => [newinternship, ...prev]);
        });
    };
    useEffect(()=>{
        getInternship();
    }, [])
    const [editing, setEditing] = useState(false)
    const [newInternshipLink, setNewInternshipLink] = useState();
    const [newInternshipURL, setNewInternshipURL] = useState();
    const [newTitle, setNeTitlek] = useState();
    const [newSubtitle, setNewSubtitle] = useState();




    return (
        <>
            <div className='title'>홈탭의 UKOV후기와 프로그램탭의 스타트업 인턴십 <b>두곳 모두</b> 수정되는 곳입니다!</div>
            <div className='subtitle'> 추가하기 </div>
            <input type='file' onClick='' value=''/>
            <input type='text' placeholder='링크 주소를 입력하세요' onClick='' value=''/>
            <input type='text' placeholder='제목을 입력해주세요' onClick='' value=''/>
            <input type='text' placeholder='부제를 입력해주세요' onClick='' value=''/>
            <input type='submit' value=''/>


            <div className='subtitle'>수정하기</div>


            {internship.map((internship)=>(
                  <div className='listBox'>
                      <a href={internship.internshipLink}>
                          <div className='smallBox'>
                            <img src={internship.internshipURL} alt='internship'/>
                          </div>
                          <h1>{internship.title}</h1>
                          <h2>{internship.subtitle}</h2>
                      </a>
                  </div>
              ))}

            <input />

        </>
    )
}

export default Internship;
