import React, { Component } from "react";
import Slider from "react-slick";
import SBVA from './SBVA.png';
import '../css/Carousel.css';
import {dbService} from '../fbase.js'


function Carousel (){

class Slide extends React.Component {

    render() {
        const settings = {
          className: "center",
          centerMode: true,
          infinite: true,
          centerPadding: "60px",
          slidesToShow: 3,
          //이것도 반응형으로 해야겠다 위에있는거!
          speed: 500
        };
        return (
          <div>
            <h2>Center Mode</h2>
            <Slider {...settings}>
              <div>
                <h3>1</h3>
              </div>
              <div>
                <h3>2</h3>
              </div>
              <div>
                <h3>3</h3>
              </div>
              <div>
                <h3>4</h3>
              </div>
              <div>
                <h3>5</h3>
              </div>
              <div>
                <h3>6</h3>
              </div>
            </Slider>
          </div>
        );
      }
    }

    return (
        <>
        <Slide/>
        </>
    )

}

export default Carousel;
