import React, { useEffect, useState,Component } from "react";
import Slider from "react-slick";
import '../css/Carousel.css';
import {dbService} from '../fbase.js'


function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "#d3d3d3", borderRadius: "100px"}}
        onClick={onClick}
      />
    );
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "#d3d3d3", borderRadius: "100px"}}
        onClick={onClick}
      />
    );
  }

  export default class Carousel extends Component {

    render() {
      const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
      };

      const getReview = async () =>{
        const dbreview = await dbService.collection("review").get();
        dbreview.forEach((document) => {
            const newReview = {
                ...document.data(),
                id: document.id
            };
            setReview((prev) => [newReview, ...prev]);
        });
    };
    useEffect(()=>{
        getReview();
    }, [])
      return (
        <div>
          <h2>Custom Arrows</h2>
          <Slider {...settings}>
          {review.map((review)=>(
                <div className='listBox'>
                    <a href={review.reviewLink}>
                        <div className='smallBox'>
                            <img src={review.reviewURL}/>
                        </div>
                        <h1>{review.title}</h1>
                        <h2>{review.subtitle}</h2>
                    </a>
                </div>
            ))}
            <div>
              <h3>2</h3>
            </div>
            <div>
              <h3>3</h3>
            </div>
            <div>
              <h3>4</h3>
            </div>
            <div>
              <h3>5</h3>
            </div>
            <div>
              <h3>6</h3>
            </div>
          </Slider>
        </div>
      );
    }
  }
