import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import '../css/Carousel.css';
import {dbService} from '../fbase.js'


function Carousel (){

    let [review, setReview] = useState([])


    const getReview = async () =>{
        const dbreview = await dbService.collection("review").get();
        dbreview.forEach((document) => {
            const newReview = {
                ...document.data(),
                id: document.id
            };
            setReview((prev) => [newReview, ...prev]);
        });
    };
    useEffect(()=>{
        getReview();
    }, [])


class Slide extends React.Component {

    render() {
        const settings = {
          className: "center",
          centerMode: true,
          infinite: true,
          centerPadding: "60px",
          slidesToShow: 3,
          //이것도 반응형으로 해야겠다 위에있는거!
          speed: 500
        };
        return (
          <div>
            <h2>Center Mode</h2>
            <Slider {...settings}>
                {review.map((review)=>(
                    <>
                    <a className='listBox' href={review.reviewLink}>
                        <div className='smallBox'>
                            <img src={review.reviewURL}/>
                        </div>
                        <h1>{review.title}</h1>
                        <h2>{review.subtitle}</h2>
                    </a>
                    </>
                ))}
            </Slider>
          </div>
        );
      }
    }

    return (
        <>
        <Slide/>
        </>
    )

}

export default Carousel;
