                    dbService.doc(`partner/${partner.id}`).delete()
                    storageService.refFromURL(partner.partnerURL).delete()
                    alert('삭제를 완료했습니다. 새로고침 하시면 확인 가능합니다!')
                }
                }}>삭제하기</button>
                </div>
            </>
        ))}         */}
    </>
)  

}

export default Partner;
