import React, { useState } from 'react';
import { useMediaQuery } from 'react-responsive';
import Menu from './menu.png';
import Logo from './logo.png';

import '../css/Navbar.css';


function Navbar(props) {

  const Mobile = useMediaQuery ({
    query : "(max-width : 1000px)"
    })
  const Web = useMediaQuery ({
    query : "(min-width : 1000px)"
    })

  const [toggle, setToggle] = useState(false)

  return (  
    <>
      {Mobile && 
      <header>
        <img src={ Logo } alt='logo' onClick={props.Home}></img>
        <img src={ Menu } alt='menu' onClick={setToggle(!toggle)}></img>
        {toggle ?(          
          <nav className='mobile'>
            <div onClick={props.Home}>home</div>
            <div onClick={props.Program}>program</div>
            <div onClick={props.Recruit}>recruit</div>
          </nav>
        ) : null
        }
      </header>
      }
      {Web &&     
      <header>
        <img className="logo" src={ Logo } alt='logo' onClick={props.Home}></img>
        <nav>
          <div onClick={props.Home}>home</div>
          <div onClick={props.Program}>program</div>
          <div onClick={props.Recruit}>recruit</div>
        </nav>
      </header>
      }
    </>
  )
}



export default Navbar;
