import React, { useEffect, useState } from 'react';
import { dbService, storageService } from '../fbase';
import { v4 as uuidv4 } from "uuid";



function Recruit({userObj}) {

    // 삭제할 때 어떤 것을 삭제할 지 확인하기 위해 기존 데이터를 불러옴
    const [recruit, setrecruit] = useState([])
    const getrecruit = async () =>{
        const dbrecruit = await dbService.collection("recruit").orderBy('createdAt').get();
        dbrecruit.forEach((document) => {
            const newrecruit = {
                ...document.data(),
                id: document.id
            };
            setrecruit((prev) => [newrecruit, ...prev]);
        });
    };
    useEffect(()=>{
        getrecruit();
    }, [])


    // 새로 추가할 스트링 state 선언
    const [newrecruitLink, setNewrecruitLink] = useState();
    const [newrecruitURL, setNewrecruitURL] = useState();
    const [newTitle, setNewTitle] = useState();
    const [newtext1, setNewtext1] = useState();
    const [newtext2, setNewtext2] = useState();
    const [newtext3, setNewtext3] = useState();
    const [newtext4, setNewtext4] = useState();
    const [newtext5, setNewtext5] = useState();
    const [newtext6, setNewtext6] = useState();
    const [newtext7, setNewtext7] = useState();
    const [newtext8, setNewtext8] = useState();



    // input태그에 입력 시 각 state에 정보 업로드
    const onLinkChange = (event) => {
        const {target:{value}} = event;
        setNewrecruitLink(value)
    }
    const onrecruitURLChange = (event) => {
        const {target:{value}} = event;
        setNewrecruitURL(value)
    }
    const onTitleChange = (event) => {
        const {target:{value}} = event;
        setNewTitle(value)
    }
    const ontext1Change = (event) => {
        const {target:{value}} = event;
        setNewtext1(value)
    }
    const ontext2Change = (event) => {
        const {target:{value}} = event;
        setNewtext2(value)
    }
    const ontext3Change = (event) => {
        const {target:{value}} = event;
        setNewtext3(value)
    }
    const ontext4Change = (event) => {
        const {target:{value}} = event;
        setNewtext4(value)
    }
    const ontext5Change = (event) => {
        const {target:{value}} = event;
        setNewtext5(value)
    }
    const ontext6Change = (event) => {
        const {target:{value}} = event;
        setNewtext6(value)
    }
    const ontext7Change = (event) => {
        const {target:{value}} = event;
        setNewtext7(value)
    }
    const ontext8Change = (event) => {
        const {target:{value}} = event;
        setNewtext8(value)
    }

        // 파일을 데이터 url로 받아서 화면상에 보여주기
        const [newFile, setNewFile] = useState()
        const fileChange = (event) => {
            const {
                target: {files}
            } = event;
            const theFile = files[0];
            const reader = new FileReader();
            reader.onloadend = (finishedEvent) => {
                setNewFile(finishedEvent.currentTarget.result)
            };
            reader.readAsDataURL(theFile);
        };


    // submit 함수 
    const newSubmit = async (event) => {
        event.preventDefault();

        // 파일을 스토리지에 업로드하기
        const fireRef = storageService.ref().child(`recruit/${uuidv4()}`)
        const response = await fireRef.putString(newFile, "data_url")
        const recruitURL = await response.ref.getDownloadURL();


        // 스트링 정보를 넣어주기
        await dbService.collection("recruit").add({
            title: newTitle,
            recruitLink: newrecruitLink,
            recruitURL,
            text1: newtext1,
            text2: newtext2,
            text3: newtext7,
            text4: newtext7,
            text5: newtext7,
            text6: newtext7,
            text7: newtext7,
            text8: newtext7,
            createdAt: Date.now(),
        })

        // 새로 업로드 후 input 태그 초기화
        setNewrecruitLink('')
        setNewrecruitURL('')
        setNewTitle('')
        setNewtext1('')
        setNewtext2('')
        setNewtext3('')
        setNewtext4('')
        setNewtext5('')
        setNewtext6('')
        setNewtext7('')
        setNewtext8('')
        setNewFile()
        alert('홈페이지에서 확인해보세요 :)')
    }



    return (
        <>
            <div className='title'>홈탭의 UKOV후기와 프로그램탭의 스타트업 인턴십 <b>두곳 모두</b> 수정되는 곳입니다!</div>
            <div className='subtitle'> 추가하기 </div>

                <form onSubmit={newSubmit}>
                이미지 파일을 추가하세요<input type='file' onChange={fileChange}/>
                {newFile && <img src={newFile} alt='file' width='300px' height='300px' />}<br/>
                    <input  type='text' value={newrecruitLink} onChange={onLinkChange} placeholder='링크 주소를 입력하세요'/><br/>
                    <input  type='text' value={newTitle} onChange={onTitleChange} placeholder='회사 이름을 입력해주세요'/><br/>
                    <input  type='text' value={newrecruitURL} onChange={ontext1Change} placeholder='직무를 입력해주세요'/><br/>
                    <input  type='submit' value="추가하기" className='button'/>
                </form>
            <div className='subtitle'>삭제하기{recruit.id}</div>

        <div className='recruitbox'>
          {recruit.map((recruit)=>(
              <>
                <a href={recruit.recruitLink}>
                    <div className='imgbox'><img src={recruit.recruitURL} alt={recruit.title}/></div>
                    <h5>{recruit.title}</h5>
                    <p>{recruit.text1}</p>
                    <p>{recruit.text2}</p>
                    <p>{recruit.text3}</p>
                    <p>{recruit.text4}</p>
                    <p>{recruit.text5}</p>
                </a>

                    <button onClick={()=>{
                        // 컨펌 후 firesore에서 데이터 삭제, storage에서 데이터 삭제
                        const del = window.confirm("지우시겠습니까?");
                            if (del) {
                        dbService.doc(`recruit/${recruit.id}`).delete()
                        storageService.refFromURL(recruit.recruitURL).delete()
                        alert('삭제를 완료했습니다. 새로고침 하시면 확인 가능합니다!')
                    }
                    }}>삭제하기</button>
                </>
                ))}    
                </div> 
        </>
    )
}

export default Recruit;
