import React, { useState } from 'react';
import { dbService } from '../fbase';

function Info() {
    let [infoNum, setInfoNum] = useState(0)
    const onSubmit = (event) => {
        event.preventDefault();
        dbService.collection("UKOV").doc({infoNum}).update({
            test: "test3",
        })
        setInfoNum(infoNum+1)
    }
    return (
        <>
        <div>페이지에 들어갈 전반적인 정보들을 수정합니다.</div>
        <button onClick={onSubmit}></button>
        </>
    )
}

export default Info;
