import React, { useEffect, useState } from 'react';
import { dbService, firebaseInstance } from '../fbase';

function Info() {
    const [info, setInfo] = useState();
    const getInfo = async () =>{
        const dbinfo = await dbService.collection("UKOV").get();
        dbinfo.forEach((document) => {
            const newInfo = {
                ...document.data(),
                id: document.id
            };
            setInfo(newInfo)
        });
        };
    let [newLink, setNewLink] = useState(info?.Link)
    const newLinkChange = (event) => {
        const {target :{value}} = event;
        setNewLink(value)
    }
    let [newFinish, setNewFinish] = useState(info?.Finish)
    const newFinishChange = (event) => {
        const {target :{value}} = event;
        setNewFinish(value)
    }
    const onSubmit = (event) => {
        event.preventDefault();
        dbService.collection("test").doc("ukovInfo").Set({
            Link: newLink,
            alumni: "fail",
            end_date: "",
            start_date: "",
            file: "",
            meeting1: "",
            meeting2: "",
            project: 23,
            resumePass: '',
            th: 24,
            year: 32,
            Finish: firebaseInstance.firestore.Timestamp.fromDate(new Date(newFinish)),
        })
    }

    useEffect(()=>{
        getInfo();         
    }, [])

    return (
        <>
                <div>페이지에 들어갈 전반적인 정보들을 수정합니다.</div>

        <form onSubmit={onSubmit}>
        <input value={newLink} onChange={newLinkChange} type='text' placeholder={info?.Link}></input>구글 독스, typeform등 서류 받을 링크를 넣어주세요<br/>
        <input value={newFinish} onChange={newFinishChange} type='date' placeholder={info?.Fnish}></input>서류 마감날짜를 넣어주세요


        <button onClick={onSubmit}>수정하기</button>
        </form>

        </>
    )
}

export default Info;
