import React, { useEffect, useState } from 'react';
import { dbService, firebaseInstance } from '../fbase';

function Info() {
    const [info, setInfo] = useState();
    const onChange = (e) => {e.target.value}
    const getInfo = async () =>{
        const dbinfo = await dbService.collection("UKOV").get();
        dbinfo.forEach((document) => {
            const newInfo = {
                ...document.data(),
                id: document.id
            };
            setInfo(newInfo)
        });
        };
    let [newLink, setNewLink] = useState(info?.Link)
    const onSubmit = (event) => {
        event.preventDefault();
        dbService.collection("test").doc("ukovInfo").set({
            Link: newLink,
            alumni: "fail",
            end_date: "",
            start_date: "",
            file: "",
            meeting1: "",
            meeting2: "",
            project: 23,
            resumePass: '',
            th: 24,
            year: 32,
            Finish: firebaseInstance.firestore.Timestamp.fromDate(new Date("23:59:59"))
        })
    }
    useEffect(()=>{
        getInfo();         
    }, [])

    return (
        <>
        <form onSubmit={onSubmit}>
        <div>페이지에 들어갈 전반적인 정보들을 수정합니다.</div>
        <input value={newLink} onChange={onChange}></input>
        <button onClick={onSubmit}>수정하기</button>
        </form>

        </>
    )
}

export default Info;
